import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Home from './Components/Home';

import './App.scss';

const endpoint = 'https://localhost:8080/api';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allEmployees: [],
      isLoaded: false
     };

     this.getAllEmployees = this.getAllEmployees.bind(this);
   };

   componentDidMount() {
     this.getAllEmployees();
     this.setState({ isLoaded: 'true'});
    }


   /* {@func} getAllEmployees - fetches list of products and updates the state
    * @return {Object} Array of objects
    */
   getAllEmployees() {
     return fetch(endpoint)
      .then(res => res.json())
      .then(data => {

        this.setState({
          allEmployees: data
        });
        return data;
      });
    };

    render() {
      return (
        <Router>
            <header className="app-header">
              <span className="user"></span>
              <span className="add">+</span>
            </header>
            <body className="app-body">
              <h1>Employee Directory</h1>
              <div className="search">
                <form className="product-form" onSubmit={this.onSubmit}>
                  <input
                    type="text"
                    placeholder="John Doe..."
                    ref={query => {this.query = query}}
                  />
                <button>Go</button>
                </form>
              </div>
              <ul className="nav">
                <li>
                  <Link className="app-link colorTurq" to="/" >By Name
                    <p> See all employees listed in alphabetical order by name</p>
                  </Link>
                </li>
                <li>
                  <Link className="app-link colorGrass" to="/department">By Department
                  <p> See all Departments listed in alphabetical order in the company</p>
                </Link>
                </li>
                <li>
                  <Link className="app-link colorLime" to="/location">By Location
                    <p>See all Departments listed in alphabetical order in the company</p>
                    </Link>
                </li>
              </ul>
            </body>

            <Route path="/" component={() => <Home/>}
            />
        </Router>
      );
    }
}

export default App;
