import React, { Component } from 'react';

// Internal Routes
import Employee from './Employee';
import Create from './Create';
import Search from './Search';

// Style Routes
import '../App.scss';

class Employees extends Component {
  constructor(props) {
    super(props);

     this.onEdit = this.props.onEdit.bind(this);
     this.onDelete = this.props.onDelete.bind(this);
     this.onCreate = this.props.onCreate.bind(this);
  };

  render() {
    return (
      <div className="app-body">
        <div className="app-container">
          <Search />
          <div className="header"><h2>Listing {this.props.category}</h2></div>
          <div className="employees">
            <Create
              locations={this.props.locations}
              getAllEmployees={this.props.getAllEmployees}
              onCreateSubmit={this.props.onCreate}/>

            {
              this.props.employees.length > 0 ? (
                this.props.employees.map(employee => {
                  return (
                    <Employee
                        key={employee.id}
                        id={employee.id}
                        name={employee.name}
                        email={employee.email}
                        phone={employee.phone}
                        image={employee.image}
                        position={employee.position}
                        location={employee.location}
                        locations={this.props.locations}

                        getAllEmployees={this.props.getAllEmployees}
                        onEditSubmit={this.props.onEdit}
                        onDeleteSubmit={this.props.onDelete}/>
                  )
                })
              ) :(<div><h4 className="no-employee-msg"> There are currently no Employees</h4></div>)
            }
          </div>
        </div>
      </div>
    );
  }
};

export default Employees;
