import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Header from './Components/Header';
import Base from './Components/Base';
import List from './Components/List';


import './App.scss';

const endpoint = '/api/allEmployees';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allEmployees: [],
      isLoaded: false,
     };

     this.getAllEmployees = this.getAllEmployees.bind(this);
   };

   componentDidMount() {
     this.getAllEmployees();
     this.setState({loaded: true})
    }

   /* {@func} getAllEmployees - fetches list of products and updates the state
    * @return {Object} Array of objects
    */
   getAllEmployees() {
     return fetch(endpoint)
      .then(res => res.json())
      .then(data => {
        console.log('GOT DATA: ', data);
        this.setState({
          allEmployees: data
        });
        console.log('state set: ', this.state.allEmployees);
      });
    };

    render() {
      return (
        <Router>
            <Header />
              <Route path="/" component={() => <Base/>}
              />
          <Route path="/by-name" component={() => <List
              allEmployees={this.state.allEmployees}
              category="by name"
              theme="color-Turq"
              />}
            />
        </Router>
      );
    }
}

export default App;
