import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Home from './Components/Home';

import './App.scss';

const endpoint = 'https://localhost:8080/api';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      allEmployees: [],
      isLoaded: false
     };

     this.getAllEmployees = this.getAllEmployees.bind(this);
   };

   componentDidMount() {
     this.getAllEmployees();
     this.setState({ isLoaded: 'true'});
    }


   /* {@func} getAllEmployees - fetches list of products and updates the state
    * @return {Object} Array of objects
    */
   getAllEmployees() {
     return fetch(endpoint)
      .then(res => res.json())
      .then(data => {

        this.setState({
          allEmployees: data
        });
        return data;
      });
    };

    render() {
      return (
        <Router>
            <header className="App-header">
              <h1 className="header-text">Employee Directory</h1>
              <div className="search">

              </div>
              <ul className="nav">
                <li>
                  <Link className="app-link colorTurq" to="/" >Alphabetical</Link>
                </li>
                <li>
                  <Link className="app-link" to="/department">By Department</Link>
                </li>
                <li>
                  <Link className="app-link" to="/location">Position</Link>
                </li>
              </ul>
            </header>

            <Route path="/" component={() => <Home/>}
            />
        </Router>
      );
    }
}

export default App;
