import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Search from './Search';

import '../App.scss';

class Base extends Component {
    render() {
      return (
        window.location.pathname === '/' ?
          (<div className="app-body">
          <h1>Employee Directory</h1>
          <Search />
          <ul className="nav">
            <li>
              <Link className="app-link bgTurq" to="/by-name" >
                <span className="action">By Name</span>
                <p> See all employees listed in alphabetical order by name</p>
              </Link>
            </li>
            <li>
              <Link className="app-link bgGrass" to="/department">
                <span className="action">By Department</span>
              <p> See all Departments listed in alphabetical order in the company</p>
            </Link>
            </li>
            <li>
              <Link className="app-link bgLime" to="/location">
                <span className="action">By Location</span>
                <p>See all Locations listed in alphabetical order in the company</p>
                </Link>
            </li>
          </ul>
        </div>)
      :
        (<div className="bg"></div>)
    );
  };
};

export default Base;
